/*
 * I'm a lazy guy, so i used this script
 * https://codepen.io/ninaregli/pen/OjeMLP
 * to calculate the current scroll position
 *
 * Will be used to add/remove the additional
 * css classes to show the sticky navbar
 */
import ThemeSwitcher from "~/components/custom/ThemeSwitcher.vue";
export default {
  components: {
    ThemeSwitcher
  },
  props: {
    disableScroll: {
      type: Boolean,
      default: true
    },
    theme: {
      type: String
    },
    navigation: {
      type: Object
    }
  },
  data: function () {
    return {
      scrollPosition: null,
      headerHeight: 0,
      isOpen: false,
      search: "",
      open: false,
      active: null,
      currentLink: -1
    };
  },
  methods: {
    updateScroll() {
      this.scrollPosition = window.scrollY;
    },

    setHeaderHeight(height) {
      this.headerHeight = height;
    },

    result() {
      window.location.href = `/search?query=${this.search}`;
    },

    setActive(index) {
      this.active = index;
      this.open = !this.open;
    },

    close(e) {
      if (!this.$el.contains(e.target)) {
        this.open = false;
        this.active = !this.active;
        this.isOpen = !this.isOpen;
      }
    },

    clicked() {
      this.isOpen = false;
    }

  },

  mounted() {
    if (!this.disableScroll) {
      var height = document.getElementById("header").clientHeight;
      this.setHeaderHeight(height);
      window.addEventListener("scroll", this.updateScroll);
    }

    document.addEventListener("click", this.close);
  },

  beforeDestroy() {
    document.removeEventListener("click", this.close);
  }

};
